<footer class="container" layout="row" layout-align="space-between stretch"  ng-controller="footerCtrl" layout-wrap>        <div class="column1" flex-xs="100" flex-sm="50" flex-md="25" flex-gt-md="25" ng-cloak>            <img src="images/logo/logo-big.png" alt="">            <span>{{$trans->footer_description}}</span>        </div>        <div  class="column2" flex-xs="100" flex-sm="50" flex="25" layout="column" layout-align="space-between stretch" ng-cloak>            <md-button data-toggle="modal" data-target="#getEvent" class="md-raised">{{$trans->header_button_getevent}} <br> <strong>{{$trans->footer_getevent_application}}</strong></md-button>            <div class="shareBox">                <span class="fontLow">{{$trans->footer_social_share}}</span>                <script async type="text/javascript" src="//yastatic.net/es5-shims/0.0.2/es5-shims.min.js" charset="utf-8"></script>                <script async type="text/javascript" src="//yastatic.net/share2/share.js" charset="utf-8"></script>                <div class="ya-share2" data-services="vkontakte,facebook,odnoklassniki,twitter" data-counter=""></div>            </div>            <span class="fontLow">{{$trans->footer_contact_title}}</span>                            <div layout="column" layout-align="space-between stretch" class="fontLow">                <div layout="row" layout-align="space-between">                    <span>{{$trans->footer_contact_title_email_title}}</span>                    <a href="mailto:{{$trans->contact_email}}" class="fontRed">{{$trans->contact_email}}</a>                </div>                <div layout="row" layout-align="space-between">                    <span>{{$trans->footer_contact_form_title}}</span>                    <a href="javacript:void(0)" class="fontRed" data-toggle="modal" data-target="#makeContact">{{$trans->footer_contact_form}}</a>                </div>            </div>        </div>        <div class="column3" flex-xs="100" flex-sm="50" flex="25" layout="column" layout-align="space-between center" ng-cloak>            <div>                <ul class="footer-menu">                        <li><a href="{{ route('get_slug', 'about') }}">{{$trans->footer_links_about}}</a></li>                        <li><a href="{{ route('get_slug', 'contacts') }}">{{$trans->footer_links_contacts}}</a></li>                        <li><a href="{{ route('get_slug', 'advert') }}">{{$trans->footer_links_advert}}</a></li>                        <li><a href="{{ route('get_slug', 'copyright') }}">{{$trans->footer_links_rights}}</a></li>                        <li><a href="{{ route('get_slug', 'sitemap') }}">{{$trans->footer_links_sitemap}}</a></li>                        <li><a href="{{ route('news') }}">{{$trans->footer_links_news}}</a></li>                        <li><a href="{{ route('reports') }}">{{$trans->footer_links_reports}} </a></li>                        <li><a href="{{ route('promo') }}">{{$trans->footer_links_promo}}</a></li>                        <li><a href="{{ route('promo') }}">{{$trans->footer_links_exlusive}}</a></li>                        <li><a href="{{ route('categories') }}">{{$trans->footer_links_categories}}</a></li>                </ul>            </div>            <md-button class="md-raised" data-toggle="modal" data-target="#putEvent" >                {{$trans->footer_button_putevent}}            </md-button>            <span class="fontLow">{{$trans->footer_copyright_title}}</span>            <span class="fontRed">{{$trans->footer_copyright}}</span>        </div>        <div  class="column4" flex-xs="100" flex-sm="50" flex="25" layout="column"  layout-align="space-between stretch" ng-cloak>            <div class="mailer">                <span>{{$trans->footer_mailer_title}}</span>                <form name="projectForm" class="mailerForm" role="form" method="post" action="{{route('send-email')}}">                {!! Form::token() !!}                <div class="form-group">                    <md-input-container md-no-float class="md-block">                        <input required type="email" name="clientEmail" ng-model="project.clientEmail"                               minlength="4" ng-pattern="/^.+@.+\..+$/" class="form-control" placeholder="{{$trans->modal_placeholder_email}}" />                        <div ng-messages="projectForm.clientEmail.$error" role="alert">                          <div ng-message="required">{{$trans->modal_validation_required}}</div>                                        <div ng-message="minlength">{{$trans->modal_validation_email}}</div>                                        <div ng-message="pattern">{{$trans->modal_validation_email_pattern}}</div>                        </div>                    </md-input-container>                </div>                <md-button type="submit" class="md-raised md-warn md-block redButton">{{$trans->mailer_subscribe}}</md-button>                </form>            </div>            <div layout="column" layout-align="stretch" class="socialWrapper">                <span>{{$trans->footer_social_links}}</span>                <div layout-wrap layout="row" class="socialBtn"  layout-align="space-between center" >                    <div flex-sm=16 flex-gt-sm=33>                        <md-button ng-href="https://plus.google.com/101538583435421575473" target="_blank" class="md-fab md-mini google" aria-label="google-plus">                            <md-icon md-svg-src="images/icons/google-plus-logo.svg"></md-icon>                        </md-button>                    </div>                    <div flex-sm=16 flex-gt-sm=33 >                        <md-button ng-href="https://twitter.com/allevents_md" target="_blank" class="md-fab md-mini twitter" aria-label="twitter">                            <md-icon md-svg-src="images/icons/twitter-logo.svg""></md-icon>                        </md-button>                    </div>                    <div flex-sm=16 flex-gt-sm=33>                        <md-button ng-href="https://www.youtube.com/channel/UCXkJ3JzxQj8J7op02HW2JCQ" target="_blank" class="md-fab md-mini youtube" aria-label="youtube">                            <md-icon md-svg-src="images/icons/youtube.svg""></md-icon>                        </md-button>                    </div>                    <div flex-sm=16 flex-gt-sm=33>                        <md-button ng-href="https://www.facebook.com/allevents.md" target="_blank" class="md-fab md-mini facebook" aria-label="facebook">                            <md-icon md-svg-src="images/icons/facebook-logo.svg""></md-icon>                        </md-button>                    </div>                    <div flex-sm=16 flex-gt-sm=33>                        <md-button ng-href="https://vk.com/allevents_md" target="_blank" class="md-fab md-mini vkontakte" aria-label="vkontakte">                            <md-icon md-svg-src="images/icons/vk-logo.svg""></md-icon>                        </md-button>                    </div>                    <div flex-sm=16 flex-gt-sm=33>                        <md-button ng-href="https://www.instagram.com/alleventsmd/" target="_blank" class="md-fab md-mini instagram" aria-label="instagram">                            <md-icon md-svg-src="images/icons/instagram-logo.svg""></md-icon>                        </md-button>                    </div>                </div>            </div>        </div></footer>